export class ShoppingService {
  constructor() {
    this.ingredients = [];
  }

  getIngredients() {
    return this.ingredients;
  }

  addIngredients(ingredient) {
    this.ingredients.push(ingredient);
  }

  addIngrefientsFromRecipe(ingredient) {
    this.ingredients.push(...ingredient);
  }

  onClearIngredients() {
    this.ingredients.splice(0, this.ingredients.length);
  }

}
